const FORMAT_ONE="00:00:00",FORMAT_TWO="00:00",SECONDS=60,MINUTES=60,HOURS=24;export default class Timer{constructor(t){this.seconds=0,this.minutes=0,this.hours=0,this.time=0,this.id=t.idContainer,this.format=t.timerFormat,this.formatFull=!1,this.interval}getCurrentTime(){return document.getElementById(this.id).textContent}addZero(t){return t<10?`0${t}`:t}applyFormat(t,s,i){return"00:00:00"===this.format?(this.formatFull=!0,this.addZero(t)+":"+this.addZero(s)+":"+this.addZero(i)):"00:00"===this.format?this.addZero(s)+":"+this.addZero(i):void 0}getTime(){return this.seconds++,60===this.seconds&&(this.minutes++,this.seconds=0,60===this.minutes&&(this.hours++,this.minutes=0,this.seconds=0,24===this.hours&&(this.hours=0,this.minutes=0,this.seconds=0))),this.applyFormat(this.hours,this.minutes,this.seconds)}start(){this.interval=setInterval((()=>{this.time=this.getTime(),document.getElementById(this.id).innerHTML=this.time}),1e3)}pause(){clearInterval(this.interval)}stop(){this.seconds=0,this.minutes=0,this.hours=0,this.time=0,clearInterval(this.interval),this.formatFull?document.getElementById(this.id).innerHTML="00:00:00":document.getElementById(this.id).innerHTML="00:00"}}